// WORKER LOGIC - LINKEDIN PUBLISHER
// Receives message from Router. Only handles Publishing logic.

let msg = items[0].json.message;
// Fallback if needed
if (!msg && $('Execute Workflow Trigger')) {
    try { msg = $('Execute Workflow Trigger').first().json.message; } catch(e){}
}

if (!msg) return [];

const reply = msg.reply_to_message;
const text = (msg.text || "").trim();

// 2. ROUTE TO LINKEDIN (Must be a reply)
// Note: Even though Router sends replies here, we double check.
if (!reply) {
  return [];
}

// 3. Determine Mode (Draft vs Custom)
let contentToPost = "";
let mode = "custom";

if (/^(ok|vale|si|yes|publicar)$/i.test(text)) {
   // Mode: OK (Draft)
   mode = "draft";
   // Extract content between dashed lines if available
   const regex = /-{10,}\n([\s\S]*?)\n-{10,}/;
   const match = reply.text.match(regex);
   if (match && match[1]) {
     contentToPost = match[1].trim();
   } else {
     contentToPost = reply.text; 
   }
} else {
   mode = "custom";
   contentToPost = text;
}

if (!contentToPost) return [];

// Return item to Output Index 0
return { json: { content: contentToPost, mode } };
