// --- 1. OBTENER INPUTS ---
// Buscamos el mensaje original del usuario en el nodo Trigger
// (Aseg√∫rate de que el nodo se llame "Telegram Trigger", si no, ajusta el nombre aqu√≠)
const triggerData = $('Telegram Trigger').first();
const userMsg = triggerData ? triggerData.json.message.text : "1"; // Fallback para tests

// Buscamos los datos de Google Sheets (Input directo de este nodo)
// Cogemos la √öLTIMA fila, que es la de hoy
const lastRow = items[items.length - 1].json;

// --- 2. VALIDACI√ìN ---
const selection = parseInt(userMsg);

if (isNaN(selection) || selection < 1 || selection > 4) {
  // Si no es un n√∫mero v√°lido, paramos (o devolvemos un aviso)
  return { json: { error: "‚ö†Ô∏è Input no v√°lido. Escribe un n√∫mero del 1 al 4.", valid: false } };
}

// --- 3. EXTRACCI√ìN DEL TEMA ---
let memory;
try {
  memory = JSON.parse(lastRow.data_json);
} catch (e) {
  return { json: { error: "‚ùå Error cr√≠tico: La memoria del d√≠a est√° corrupta.", valid: false } };
}

// Array empieza en 0, as√≠ que restamos 1 (Input "1" = Index 0)
const topic = memory.topics[selection - 1];

if (!topic) {
  return { json: { error: "‚ùå Tema no encontrado (Index out of bounds).", valid: false } };
}

// --- 4. PREPARAR PAQUETE PARA LA IA ---
return {
  json: {
    valid: true,
    user_selection: selection,
    topic_title: topic.title,
    topic_rationale: topic.rationale,
    topic_sources: topic.sources ? topic.sources.join(", ") : "Varias fuentes",
    system_instruction: "Genera un post de LinkedIn viral, t√©cnico y directo."
  }
};

// 1. Limpieza JSON (Igual que antes)
const root = items[0].json;
const rawText = (root.content && root.content.parts) ? root.content.parts[0].text : root.text;

// Buscador de llaves para evitar basura
const firstBrace = rawText.indexOf('{');
const lastBrace = rawText.lastIndexOf('}');
let cleanJSON = rawText;

if (firstBrace !== -1 && lastBrace !== -1) {
    cleanJSON = rawText.substring(firstBrace, lastBrace + 1);
}

// 2. Fix saltos de l√≠nea y Parseo
let content = {};
try {
    const fixedJSON = cleanJSON.replace(/\n/g, "\\n").replace(/\r/g, "");
    content = JSON.parse(fixedJSON);
} catch (e) {
    try { content = JSON.parse(cleanJSON); } catch(e2) {
        content = { linkedin_content: "‚ö†Ô∏è Error formato", blog_content: "Error" };
    }
}

// 3. RECUPERAR T√çTULO ORIGINAL
let originalTitle = "Borrador Autom√°tico";
try {
    originalTitle = $('Code in JavaScript').first().json.target_topic.title; 
} catch(e) {
    try { originalTitle = $('Code in JavaScript').first().json.topic_title; } catch(e2) {}
}

// SALIDA ACTUALIZADA CON EL PROMPT DE IMAGEN
return {
    json: {
        title: originalTitle,
        linkedin_draft: content.linkedin_content || content.linkedin_draft,
        blog_draft: content.blog_content || content.blog_draft,
        // A√ëADIMOS ESTA L√çNEA:
        image_prompt: content.image_prompt || "Futuristic technology concept, editorial photography, 8k" // Fallback por si acaso
    }
};

