// --- 1. OBTENER INPUTS ---
const triggerData = $('Telegram Trigger').first();
const text = triggerData ? triggerData.json.message.text : "";

// --- NUEVA LÓGICA DE FILTRADO (FIX: Strict Number) ---
// Queremos que este workflow SOLO salte si el usuario escribe un NÚMERO (1-5).
// Ignoramos todo lo demás (OK, Hola, etc.) para evitar conflictos.

const selection = parseInt(text.trim());
const isNumber = !isNaN(selection) && selection >= 1 && selection <= 5;

// Si NO es un número válido 1-5 -> IGNORAR SILENCIOSAMENTE 
// (Devolvemos array vacío para que el workflow se detenga aquí)
if (!isNumber) {
  return [];
}

// Si ES 1-5, continuamos (ignorando si es reply o no)

// Buscamos los datos de Google Sheets
const lastRow = items[items.length - 1].json;

// --- 2. EXTRACCIÓN DEL TEMA ---
let memory;
try {
  memory = lastRow.data_json ? JSON.parse(lastRow.data_json) : {};
} catch (e) {
  return { json: { error: "❌ Error crítico: JSON inválido.", valid: false } };
}

const topics = memory.topics || [];
const topic = topics[selection - 1];

if (!topic) {
  return { json: { error: `❌ Tema no encontrado (${selection}).`, valid: false } };
}

// --- 3. PREPARAR PAQUETE ---
return {
  json: {
    valid: true,
    user_selection: selection,
    topic_title: topic.title,
    topic_rationale: topic.rationale,
    topic_sources: topic.sources ? topic.sources.join(", ") : "",
    system_prompt: topic.system_prompt || ""
  }
};

// 1) Limpieza JSON (igual que antes)
const root = items[0].json;
const rawText = (root.content && root.content.parts) ? root.content.parts[0].text : (root.text || "");

// Buscador de llaves para evitar basura
const firstBrace = rawText.indexOf('{');
const lastBrace = rawText.lastIndexOf('}');
let cleanJSON = rawText;

if (firstBrace !== -1 && lastBrace !== -1) {
  cleanJSON = rawText.substring(firstBrace, lastBrace + 1);
}

// 2) Fix saltos de línea y Parseo
let content = {};
try {
  const fixedJSON = cleanJSON.replace(/\n/g, "\\n").replace(/\r/g, "");
  content = JSON.parse(fixedJSON);
} catch (e) {
  try {
    content = JSON.parse(cleanJSON);
  } catch (e2) {
    content = { linkedin_content: "⚠️ Error formato", blog_content: "Error" };
  }
}

// 2.1) Normalizador de campos (por si vienen con keys raras)
const linkedin_draft = content.linkedin_draft || content.linkedin_content || "";
const blog_draft = content.blog_draft || content.blog_content || "";

const image_subject =
  content.image_subject ||
  content.imageSubject ||
  content.subject ||
  "";

const image_theme =
  content.image_theme ||
  content.image_Theme ||
  content.imageTheme ||
  content.theme ||
  "";

let image_keywords =
  content.image_keywords ||
  content.image_Keywords ||
  content.imageKeywords ||
  content.keywords ||
  [];

// keywords puede venir como string "a, b, c"
if (typeof image_keywords === "string") {
  image_keywords = image_keywords
    .split(",")
    .map(s => s.trim())
    .filter(Boolean);
}

if (!Array.isArray(image_keywords)) image_keywords = [];
image_keywords = image_keywords
  .map(k => String(k).trim())
  .filter(Boolean)
  .slice(0, 10);

// 3) RECUPERAR TÍTULO ORIGINAL (igual que antes)
let originalTitle = "Borrador Automático";
try {
  originalTitle = $('Code in JavaScript').first().json.target_topic.title;
} catch (e) {
  try { originalTitle = $('Code in JavaScript').first().json.topic_title; } catch (e2) {}
}

// 4) PROMPT FIJO (tu estilo)
const STYLE_PROMPT =
  "A realistic subject illustrated in the style of the iPad app Paper by FiftyThree. " +
  "The subject must be a real, recognizable thing from the real world (animal, person, object, or everyday scene), not a metaphor, not an abstract concept. " +
  "Hand-sketched watercolor look with loose pencil and ink lines, imperfect strokes, soft watercolor washes, and an off-white paper background. " +
  "Paper53 style as an aesthetic, not a drawing action. " +
  "No hands, no people, no body parts, no drawing tools. " +
  "No robots, no mechanical parts, no futuristic or sci-fi aesthetics. " +
  "No diagrams, no mind maps, no abstract symbols. " +
  "Text is optional and secondary; if present, use only real, common words related to the topic. " +
  "No invented words, no pseudo-text, no filler. " +
  "Calm, human, organic, editorial tone.";

// 5) Construcción del prompt final (controlado)
const subjectFinal = (image_subject && String(image_subject).trim()) || `a real-world subject related to: ${originalTitle}`;
const themeFinal = (image_theme && String(image_theme).trim()) || "";
const keywordsFinal = (image_keywords && image_keywords.length) ? image_keywords.join(", ") : "";

const finalImagePrompt = (
  `${STYLE_PROMPT}\n\n` +
  `Main subject: ${subjectFinal}.\n` +
  (themeFinal ? `Theme/context: ${themeFinal}.\n` : "") +
  (keywordsFinal ? `Optional real words (if used, short): ${keywordsFinal}.\n` : "") +
  "Prefer the image to work even with no text."
).substring(0, 999);

// 6) SALIDA FINAL
return {
  json: {
    title: originalTitle,
    linkedin_draft,
    blog_draft,
    image_subject: subjectFinal,
    image_theme: themeFinal,
    image_keywords: image_keywords,
    image_prompt: finalImagePrompt
  }
};

